
// ************************************************
// Shopping Cart API
// ************************************************

var shoppingCart = (function () {
  // =============================
  // Private methods and propeties
  // =============================
  cart = [];

  // Constructor
  function Item(name, price, count, value, idPedido, idPrenda, idTela, talla, nomTel) {
    this.name = name;
    this.price = price;
    this.count = count;
    this.value = value;
    this.idPedido = idPedido;
    this.idPrenda = idPrenda;
    this.idTela = idTela;
    this.talla = talla;
    this.nomTel = nomTel;
  }

  // Save cart
  function saveCart() {
    sessionStorage.setItem('shoppingCart', JSON.stringify(cart));
  }

  // Load cart
  function loadCart() {
    cart = JSON.parse(sessionStorage.getItem('shoppingCart'));
  }
  if (sessionStorage.getItem("shoppingCart") != null) {
    loadCart();
  }


  // =============================
  // Public methods and propeties
  // =============================
  var obj = {};

  // Add to cart
  obj.addItemToCart = function (name, price, count, value, idPedido, idPrenda, idTela, talla, nomTel) {
	  if(cart.length==0){
		  Swal.fire({
		      position: 'center',
		      icon: 'success',
		      title: '¡Añadido al Rack!',
		      showConfirmButton: false,
		      timer: 1550
		})
	  }
    for (var item in cart) {
      if (cart[item].idPedido === idPedido) {
    	  Swal.fire({
    	      position: 'center',
    	      icon: 'success',
    	      title: '¡Ya está en el Rack!',
    	      showConfirmButton: false,
    	      timer: 1550
    	})
        return;
      }
      else{
    	  Swal.fire({
    	      position: 'center',
    	      icon: 'success',
    	      title: '¡Añadido al Rack!',
    	      showConfirmButton: false,
    	      timer: 1550
    	})
      }
    }
    var item = new Item(name, price, count, value, idPedido, idPrenda, idTela, talla, nomTel);
    cart.push(item);
    saveCart();
  }
  // Set count from item
  obj.setCountForItem = function (name, count) {
    for (var i in cart) {
      if (cart[i].name === name) {
        cart[i].count = count;
        break;
      }
    }
  };
  // Remove item from cart
  obj.removeItemFromCart = function (name) {
    for (var item in cart) {
      if (cart[item].name === name) {
        cart[item].count--;
        if (cart[item].count === 0) {
          cart.splice(item, 1);
        }
        break;
      }
    }
    saveCart();
  }

  // Remove all items from cart
  obj.removeItemFromCartAll = function (name) {
    for (var item in cart) {
      if (cart[item].name === name) {
        cart.splice(item, 1);
        break;
      }
    }
    saveCart();
  }

  // Clear cart
  obj.clearCart = function () {
    cart = [];
    saveCart();
  }

  // Count cart 
  obj.totalCount = function () {
    var totalCount = 0;
    for (var item in cart) {
      totalCount += parseInt(cart[item].count);
    }
    return totalCount;
  }

  // Total cart
  obj.totalCart = function () {
    var totalCart = 0;
    for (var item in cart) {
      totalCart += cart[item].price * cart[item].count;
    }
    return Number(totalCart.toFixed(2));
  }

  // List cart
  obj.listCart = function () {
    var cartCopy = [];
    for (i in cart) {
      item = cart[i];
      itemCopy = {};
      for (p in item) {
        itemCopy[p] = item[p];

      }
      itemCopy.total = Number(item.price * item.count).toFixed(2);
      cartCopy.push(itemCopy)
    }
    return cartCopy;
  }

  // cart : Array
  // Item : Object/Class
  // addItemToCart : Function
  // removeItemFromCart : Function
  // removeItemFromCartAll : Function
  // clearCart : Function
  // countCart : Function
  // totalCart : Function
  // listCart : Function
  // saveCart : Function
  // loadCart : Function
  return obj;
})();

    	

